// localStorage.clear();

let t121 = false;
let t13 = true;
let t22 = false;
let t31 = false;
let t2cols = true;
let t3cols = false;

try {
    if (localStorage) {
        // console.log("we have localStorage")
        if (localStorage.getItem("t121") !== null){
            t121 = (localStorage.getItem("t121") === 'true');
        }
        if (localStorage.getItem("t13") !== null){
            t13 = (localStorage.getItem("t13") === 'true');
        }
        if (localStorage.getItem("t22") !== null){
            t22 = (localStorage.getItem("t22") === 'true');
        }
        if (localStorage.getItem("t31") !== null){
            t31 = (localStorage.getItem("t31") === 'true');
        }
        if (localStorage.getItem("t2cols") !== null){
            t2cols = (localStorage.getItem("t2cols") === 'true');
        }
        if (localStorage.getItem("t3cols") !== null){
            t3cols = (localStorage.getItem("t3cols") === 'true');
        }
    }
} catch (e) {console.log("we don't have localStorage")}


const template = {
    namespaced: true,

    state: {
        shouldUseTemplate121: t121,
        shouldUseTemplate13: t13, // default
        shouldUseTemplate22: t22,
        shouldUseTemplate31: t31,
        shouldUseTemplate2cols: t2cols,
        shouldUseTemplate3cols: t3cols,
    },

    mutations: {
        toggleTemplate121 (state) {
            state.shouldUseTemplate121 = true;
            state.shouldUseTemplate3cols = true;
            state.shouldUseTemplate2cols = false;
            state.shouldUseTemplate13 = false;
            state.shouldUseTemplate22 = false;
            state.shouldUseTemplate31 = false;        
        },
        toggleTemplate13 (state) {
            state.shouldUseTemplate3cols = false;
            state.shouldUseTemplate2cols = true;
            state.shouldUseTemplate121 = false;
            state.shouldUseTemplate13 = true;
            state.shouldUseTemplate22 = false;
            state.shouldUseTemplate31 = false;
        },
        toggleTemplate22 (state) {
            state.shouldUseTemplate3cols = false;
            state.shouldUseTemplate2cols = true;
            state.shouldUseTemplate121 = false;
            state.shouldUseTemplate13 = false;
            state.shouldUseTemplate22 = true;
            state.shouldUseTemplate31 = false;
        },
        toggleTemplate31 (state) {
            state.shouldUseTemplate3cols = false;
            state.shouldUseTemplate2cols = true;
            state.shouldUseTemplate121 = false;
            state.shouldUseTemplate13 = false;
            state.shouldUseTemplate22 = false;
            state.shouldUseTemplate31 = true;
        },

        saveEditing(state, payload){
            state.shouldUseTemplate3cols = payload.shouldUseTemplate3colsTmp;
            state.shouldUseTemplate2cols = payload.shouldUseTemplate2colsTmp;
            state.shouldUseTemplate121 = payload.shouldUseTemplate121Tmp;
            state.shouldUseTemplate13 = payload.shouldUseTemplate13Tmp;
            state.shouldUseTemplate22 = payload.shouldUseTemplate22Tmp;
            state.shouldUseTemplate31 = payload.shouldUseTemplate31Tmp;
        },

    },

    getters: {

        getScaleType: (state) => {
            if (state.shouldUseTemplate121){
                return "t121";
            } else if (state.shouldUseTemplate13){
                return "t13";
            } else if (state.shouldUseTemplate22){
                return "t22";
            } else if (state.shouldUseTemplate31){
                return "t31";
            }
        }
    },

    actions: {
        saveEditing({commit}, payload){
            commit("saveEditing", payload);
        }
    }

  }

  export default template
